INSERT INTO `alex_livre_messages` VALUES ('15', 'CATTEEUW Joel', '', '', ' Grimisuat', 'ch', '', 'Un tout grand merci pour l\'accueil chaleureux que nous ont réservés les propriétaires.\r\nUne location extraordinaire conforme au descriptif avec sa piscine et son sauna qui ont enchantés toute la famille.Bref un week-end de détente et de remise en forme dans un village très sympathique.\r\nUne seule envie, c\'est de revenir en Alsace chez Mme et Mr SCHLEGEL.', '', '', '1229194145', '192.168.1.20', 'yes');
