INSERT INTO `alex_livre_messages` VALUES ('21', 'Babeth', 'babeth.jeanssens@neuf.fr', '', 'Livry gargan', 'fr', '', 'Nous rentrons de 8 jours de rêve dans cette Alsace que j aime tant!!\r\nUn beau cadeau de Mariage que m a fait mon mari pour célébrer nos 30 ans de vie commune.\r\nOn ne peut rêver mieux que cette location située dans un charmant village vivant et calme à  la fois.\r\nEt que dire de cette appartement où tout y est, la beauté, la sobriété, le fonctionnel, le confort et la sensation d\'être à  la fois coocooner et libre . Une magnifique piscine et le sauna complètent les moments de détente.\r\nUn accueil chaleureux, une présence discrète mais néanmoins efficace de nos hotes  et c\'est l harmonie totale durant tout le séjour.\r\nBravo à  vous 2 c\'est sur on se reverra\r\nEt bon Anniversaire à  vous 2 qui fêtez la même occasion que nous à  quelques jours d\'écarts\r\nbien amicalement\r\nFamilles Jeanssens-Pédrotti', '', '', '1255708283', '86.73.225.53', 'yes');
INSERT INTO `alex_livre_messages` VALUES ('22', 'Yolande et joël', '', '', 'Plehedel', 'fr', '', 'nous avons passé une semaine très agréable dans votre superbe demeure que vous avez rénové avec goût : piscine ,sauna , terrasse, salle de jeux ... dans un petit village au calme , tout y est...\r\net encore merci pour votre accueil chaleureux , votre sympathie et votre disponibilité \r\n           ', '', '', '1259368914', '90.12.81.66', 'yes');
