INSERT INTO `alex_livre_messages` VALUES ('56', 'Eric, Agnès, Céline et Fabien', 'eric.eo93@gmail.com', '', 'Villemomble', 'fr', '10', 'Depuis les nombreuses années que nous venons, nous sommes toujours aussi heureux de notre séjour chez Mr et Mme SCHLEGEL. Accueil chaleureux et logement impeccable. Et pour se détendre, le bonheur d\'un bain dans la piscine ou d\'un Sauna revigorant.\r\nMerci à  vous pour ce séjour de détente et à  bientôt.\r\nAmitiés\r\n', '', '', '1425148973', '93.14.235.68', 'yes');
