INSERT INTO `alex_livre_messages` VALUES ('138', 'Chantal', '', '', 'Moselle', 'fr', '', 'Concernant la réservation du réveillon de nouvel an, nous avons été accueillis chaleureusement par les hôtes.\r\n\r\nNous avions réservé plusieurs appartements, tous étaient d\'une propreté irréprochable, et très bien équipés.\r\nMerci encore pour le cadeau d\'accueil.\r\nUne adresse à  retenir', '', '', '1642096989', '77.131.11.243', 'yes');
