INSERT INTO `alex_livre_messages` VALUES ('154', 'Famille poilane', 'celinepoilane22@gmail.com', '', 'Saint Père en Retz ', 'fr', '', 'Superbe gîte tenu par un couple d\'une grande gentillesse. Nous étions en famille dans l\'appartement le bouleau ou nous nous sommes senti très à  l\'aise. La piscine est vrai + ainsi que la salle de jeu. La salle lingerie est vraiment aussi très appréciable. Une maison tenu impeccable par les propriétaires. Nous recommandons cet endroit pou cela,pour son village charmant et sa proximité avec plein de site à  voir très joli.tres belle région.', '', '', '1688993679', '92.184.119.4', 'yes');
INSERT INTO `alex_livre_messages` VALUES ('155', 'Michael', '', '', '', 'ch', '', 'Très belle découverte d\'une magnifique région. Merci encore infiniment pour le parfait accueil.\r\nEmplacement idéal permettant de partir découvrir la région à  pied directement depuis l\'hébergement. Très calme à  côté de la rivière sans aucun bruit de trafic. La piscine était parfaite pour se détendre après les belles journées de découvertes. ', '', '', '1689002090', '89.157.19.6', 'yes');
