INSERT INTO `alex_livre_messages` VALUES ('159', 'Magali', '', '', '', 'fr', '', 'Magnifique\r\nHébergement très propre et très bien équipé avec une machine à  laver et sèche linge à  disposition dans les parties communes Piscine au top et salle de jeux bien équipée avec babyfoot et appareils de sports Le gîte est situé au calme dans une petite rue à  proximité des commerces Les propriétaires sont très gentils et aux petits soins avec leurs locataires Nous avons passé dâ€™excellentes vacances ', '', '', '1691685143', '89.157.19.6', 'yes');
