INSERT INTO `alex_livre_messages` VALUES ('161', 'Eberhard', '', '', '', 'de', '', 'Wunderbar zum Wandern und Schwimmen\r\n\r\nStille Sackgasse, Parkplatz vor der Haustäre, Haus mit 5 Ferienwohnungen wie neu. Vermieter kam sofort aus dem Nebenhaus, obwohl wir eine Stunde zu fräh ankamen, freundlicher Empfang auf deutsch. Eine Flasche Wein vom Winzer nebenan im Kählschrank. Viel freier Stauraum in den Schränken. Heizung macht kuschelig warm an kählen Winterabenden. Lange Ã–ffnungszeiten des 8m Schwimmbads, angenehme Wassertemperatur 28°. Zwei Bäcker und ein Supermarkt zu FuÃŸ zu erreichen. Herrliche Wanderung durch die Weinberge auf das Zinnküpfle von der Haustär weg.', '', '', '1696584435', '89.157.19.6', 'yes');
INSERT INTO `alex_livre_messages` VALUES ('162', 'Juju', '', '', 'AVALLON', 'fr', '', 'Des hôtes formidables, un environnement magnifique. Nous reviendrons avec certitude. Merci encore à  nos hôtes.', '', '', '1699380478', '89.157.19.6', 'yes');
